import React from 'react';
import '../styles/ProductListing.css'; // Import the CSS file
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useState } from 'react';
import { useEffect } from 'react';
import TopBar from './TopBar';
import HorizontalBar from './HorizontalBar';

const ProductDetails = () => {
  const { id } = useParams(); // Get the product ID from the URL
  const [product, setProduct] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/api/products/${id}`);
        if (response.status === 200) {
          setProduct(response.data);
        } else {
          console.error('Failed to fetch product');
        }
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    };

    fetchProduct();
  }, [id]);


  return (
    <div>
      <TopBar/>
      <div className="content-wrapper">
        <HorizontalBar/>

        {/* "Back to Products" button */}
        <button onClick={() => navigate(-1)}>Back to Products</button>

        <h2>{product ? product.productName : 'Loading...'}</h2>

        <div className="product-details">
          {/* Left side for images */}
          <div className="images">
            {/* {product && product.imageUrl.map((image, index) => (
              <img key={index} src={image} alt={`Product ${index + 1}`} />
            ))} */}
            {product && <img src={product.imageUrl} alt={product.productName} />}
          </div>

          {/* Right side for description */}
          <div className="description">
            <p>Price: ${product ? product.price : ''}</p>
            <p>Color: {product ? product.color : ''}</p>
            <p>Type: {product ? product.productType : ''}</p>
            <p>Description: {product ? product.description : ''}</p>
          </div>

        </div>
      </div>

    </div>
  );
};

export default ProductDetails;
