import React from 'react';
import '../styles/ProductListing.css'; // Import the CSS file
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { useState } from 'react';

const ProductDetails = () => {
  const { id } = useParams(); // Get the product ID from the URL
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/api/products/${id}`);
        if (response.status === 200) {
          setProduct(response.data);
        } else {
          console.error('Failed to fetch product');
        }
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    };

    fetchProduct();
  }, [id]);

  return (
    <div className="product-details-container">
      {/* Top bar */}
      <div className="top-bar">
        {/* Same as the top bar in ProductListing */}
      </div>

      {/* Horizontal bar */}
      <div className="horizontal-bar">
        {/* Same as the horizontal bar in ProductListing */}
      </div>

      {/* Back to products button */}
      <button className="back-btn">Back to products</button>

      {/* Product name */}
      <h2>{productName}</h2>

      {/* Product details */}
      <div className="product-details-wrapper">
        {/* Left side - product images */}
        <div className="product-images">
          {/* Display product images here */}
        </div>

        {/* Right side - product details */}
        <div className="product-info">
          <h3>{productName}</h3>
          {/* Star ratings and customer reviews */}
          {/* Price */}
          <p>Price: ${price}</p>
          <p>Color: {color}</p>
          <p>Type: {productType}</p>
          {/* Description */}
          <p>{description}</p>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
